import React, {useState} from 'react'
import LoginForm from "./components/LoginForm"
import RegisterForm from './components/RegisterForm'

function App() {
  const adminUser ={
    email: "123",
    password:"123"
  }
  const [guest, setGuest] = useState({name:'',email:''})
  const [error, setError] = useState('')
  const [FormType, setType] = useState('login')

  const Login = details => {
    console.log(details);
    if (details.email==adminUser.email && details.password==adminUser.password){
      console.log("Logged in")
      setGuest({
        name: details.name,
        email: details.email
      })
    } else {
      console.log("Details do not match.")
      setError("Details do not match.")
    }
  }

  const AdminUser = () =>{
  }

  // const [user, setUser]= useState=([{name:'', email:'', password:''}])


  const Logout = () => {
    console.log("Logout")
    setGuest({name:'', email:''})
  }

  const FormSwitch = () => {
    console.log('change-form');
    if(FormType === 'login'){
      setType('register')
    }
    else{
      setType('login')
    }
  }


  return (
    <div>
    <div>
      <a/>
    </div>
    <div>
      <ul>
        <li className="title">
            <h1 className="title-name">Info Exchange</h1>
        </li>
        <li className="App">
          {(guest.email != "")?(
            <div>
              <h2>Welcome, <span>{guest.name}</span></h2>
              <button onClick={Logout}>Logout</button>
            </div>
          ):(
            (FormType === "login")?(<LoginForm Login={Login} error={error} Change={FormSwitch}/>):(
              <RegisterForm AdminUser={AdminUser} Change={FormSwitch}/>))}
        </li>
      </ul>
    </div>
    </div>
  );
}

export default App;
